import './App.css';
import React , {useState} from 'react';
import img from './add.png'

function App() {
  let [item , setItem] = useState();
  let [allItem , setAllItem] = useState([]);
  const change = (value) =>{
    setItem(value.target.value)
  }
  const clicked = () =>{
    setAllItem((oldItem) =>{
      let curItem = item;
      setItem("")
      return [...oldItem , curItem];
    })
  }
  return (
    <>
      <div className = "box">
        <div className='box1'>
          <h1>ToDo List</h1>
          <div className='input'>

            <input type='input' placeholder='Add an Item' onChange={change} value="asdfq"></input>
            <button className='button' onClick={clicked}>+</button>
          </div>
          <ol>
              {allItem.map((item) =>{
                return <li>{item}</li>
              })}
          </ol>
        </div>
      </div>
    </>
  );
}

export default App;
